XExample setup_hhbquery() //returns a pointer in userspace to the device
{
    void *mapped_base_hhbquery;
    int memfd_hhbquery;

    void *mapped_dev_base;
    off_t dev_base = HHBQUERY_ADDR;

    memfd_hhbquery = open("/dev/mem", O_RDWR | O_SYNC); //to open this the program needs to be run as root
        if (memfd_hhbquery == -1) {
        printf("Can't open /dev/mem.\n");
        exit(0);
    }
    //printf("/dev/mem opened.\n"); 

    // Map one page of memory into user space such that the device is in that page, but it may not
    // be at the start of the page.

    mapped_base_hhbquery = mmap(0, MAP_SIZE, PROT_READ | PROT_WRITE, MAP_SHARED, memfd_hhbquery, dev_base & ~MAP_MASK);
        if (mapped_base_hhbquery == (void *) -1) {
        printf("Can't map the memory to user space.\n");
        exit(0);
    }
     //printf("Memory mapped at address %p.\n", mapped_base_hhbquery); 

    // get the address of the device in user space which will be an offset from the base 
    // that was mapped as memory is mapped at the start of a page 

    mapped_dev_base = mapped_base_hhbquery + (dev_base & MAP_MASK);
    XExample device;
    device.Bus_a_BaseAddress = mapped_dev_base;
    device.IsReady = 1;

    return device;
    //return mapped_dev_base;
}


